export default {
  name: 'TransitionFadeSlide',
  props: {
    appear: {
      type: Boolean,
      default: true
    },
    direction: {
      type: String,
      default: 'y'
    },
    group: {
      type: Boolean,
      default: false
    },
    mode: {
      type: String,
      default: 'out-in'
    },
    tag: {
      type: String,
      default: ''
    }
  },
  computed: {
    name: function name() {
      return "fade-slide-".concat(this.direction);
    },
    component: function component() {
      return this.group ? 'transition-group' : 'transition';
    }
  }
};
