import TransitionFadeSlide from '@theme/components/transitions/TransitionFadeSlide';
import TheHeaderNavbar from '@theme/components/TheHeaderNavbar';
import TheHeaderBanner from '@theme/components/TheHeaderBanner';
import GeoPattern from 'geopattern';
var generators = ['octogons', 'overlappingCircles', 'plusSigns', 'xes', // 'sineWaves',
'hexagons', 'overlappingRings', // 'plaid',
'triangles', // 'squares',
'concentricCircles', 'diamonds', 'tessellation', // 'nestedSquares',
'mosaicSquares', 'chevrons'];
export default {
  name: 'TheHeader',
  components: {
    TransitionFadeSlide: TransitionFadeSlide,
    TheHeaderNavbar: TheHeaderNavbar,
    TheHeaderBanner: TheHeaderBanner
  },
  computed: {
    background: function background() {
      return this.$site.themeConfig.headerBackground || {};
    },
    headerStyle: function headerStyle() {
      return this.background.url ? {
        'background-size': 'cover',
        'background-repeat': 'no-repeat',
        'background-position': 'center',
        'background-attachment': 'scroll',
        'background-image': "url(".concat(this.background.url, ")")
      } : !this.$ssrContext ? {
        // avoid prerendering the geopattern in build stage
        // or the html files will be 14KB larger
        'background-image': this.gpImg()
      } : {};
    }
  },
  methods: {
    // in computed, geopattern will always be computed
    // in methods, geopattern won't be called with useGeo = false
    gpImg: function gpImg() {
      return this.background.useGeo ? GeoPattern.generate(this.gpString(), {
        // color: '#9fe0f6',
        generator: this.gpGenerator()
      }).toDataUrl() : null;
    },
    gpString: function gpString() {
      return (this.$page && this.$page.title || '') + new Date().toString();
    },
    gpGenerator: function gpGenerator() {
      return generators[Math.floor(Math.random() * generators.length)];
    }
  }
};
